'use strict';

var tweetnacl = require('tweetnacl');

var ed25519Keypair = function ed25519Keypair(_) {
  var kp = tweetnacl.sign.keyPair();
  return [kp.publicKey, kp.secretKey];
};

var generatePublicKey = function generatePublicKey(privateKey) {
  var kp = tweetnacl.sign.keyPair.fromSecretKey(privateKey);
  return kp.publicKey;
};

var generatePairFromSeed = function generatePairFromSeed(privateKey) {
  privateKey = Uint8Array.from(privateKey);
  var kp = tweetnacl.sign.keyPair.fromSeed(privateKey);
  return [kp.publicKey, kp.secretKey];
};

var sign = function sign(message, privateKey) {
  var sig = tweetnacl.sign(message, privateKey); // By default, the signature contains the message at the end, we don't need this

  return sig.slice(0, sig.length - message.length);
};

module.exports = {
  keyPair: ed25519Keypair,
  sign: sign,
  generatePublicKey: generatePublicKey,
  generatePairFromSeed: generatePairFromSeed
};
